	    {
		return -1;
	    }
	    printf("%p\n", tm);
	    for (int i = 0; i < nr; ++i)
	    {
		if ((tm[i] = (double *) malloc ((i + 1) * sizeof (double))) == NULL)
		{
		    return -1;
		}
	    }
